import React from "react";
import ReactDOM from "react-dom";

class List extends React.Component  {
    constructor(props){
        super(props);
        this.state = {
            detalhes:''
        };

        this.Listador = this.Listador.bind(this);
        this.Alerta = this.Alerta.bind(this);
    }
    
    Listador(event) {
        this.setState({detalhes: event.target.detalhes});
    }
    
    Alerta(event) {
        alert('Tarefa Listada: ' + this.state.detalhes);
        event.preventDefault();
    }
    
    render() {
        return (
            <form onSubmit={this.Alerta}>
            <label>
            Listar:
            <input type="text" value={this.state.detalhes} onChange={this.Listador} />
            </label>
            <input type="submit" value="Enviar" />
            </form>
        );
    }
}


let domContainer = document.querySelector('#button_container');
ReactDOM.render(<List />, document.getElementById("root"));
