import React from "react";
import ReactDOM from "react-dom";

class List extends React.Component  {
    constructor(props){
        super(props);
        this.state = {
            value:[1,2]
        };

        this.mudarValor = this.mudarValor.bind(this);
        this.Alerta = this.Alerta.bind(this);
    }
    
    mudarValor(event) {
        this.setState({value: event.target.value});
    }
    
    Alerta(event) {
        alert('Tarefa Listada: ' + this.state.value);
        event.preventDefault();
    }
    
    render() {
        return (
            <div>
                <form onSubmit={this.Alerta}>
                <label>
                Adicionar:
                <input type="text" value={this.state.value} onChange={this.mudarValor} />
                </label>
                <input type="submit" value="Enviar" />
                </form>
                <ul>
                    {this.state.value.map(item => (  
                        <li key={item}>{item}</li> 
                    ) ) }
                </ul>
            </div>
        );
    }
}


let domContainer = document.querySelector('#button_container');
ReactDOM.render(<List />, document.getElementById("root"));
