import React from "react";
import ReactDOM from "react-dom";

class List extends React.Component  {
    constructor(props){
        super(props);
        this.state = {
            value:[],
            id:[]
        };

        this.mudarValor = this.mudarValor.bind(this);
        this.Alerta = this.Alerta.bind(this);
    }
    
    mudarValor(event) {
        this.setState({value: event.target.name})
        console.log(event.target.name)
    };
    
    Alerta(event) {
        alert('Tarefa Listada: ' + event.target.name);//this.state.value
        event.preventDefault();

    }
    
    render() {
        return (
            <div>
            <form onSubmit={this.Alerta}>
            <label>
            Adicionar:
            <input type="text" name={this.state.value} onSubmit={this.mudarValor}/>
            </label>
            <input type="Submit" value="Adicionar" />
            </form>
            <ul>
                {this.state.value.map(item => (
                    <li key={item}>{item}   <button>Remover</button>   <button>Marcar</button></li>
                ))}
            </ul>
            </div>
        );
    }
}


let domContainer = document.querySelector('#button_container');
ReactDOM.render(<List />, document.getElementById("root"));
