import React from "react";
import ReactDOM from "react-dom";

class List extends React.Component  {
    constructor(props){
        super(props);
        
        this.state = {
            itemList:'',
            id:1,
            save:[
                { id:1, texto: 'bom dia 1', marcado: false},
                { id:2, texto: 'bom dia 2', marcado: false}
            ]
        };

        this.mudarValor = this.mudarValor.bind(this);
        this.cadastrar = this.cadastrar.bind(this);
        this.marcar = this.marcar.bind(this);
        this.remover = this.remover.bind(this);

    }
    
    mudarValor(event) {
        this.setState((state) => {
           return {itemList: event.target.value}
        })
    };

    marcar(idparamarcar) {
        function marcatext(objtos)    {
            if(idparamarcar.id == objtos.id)  {
                return  {
                    id: objtos.id,
                    texto: objtos.texto,
                    marcardo: !objtos.marcado 
                    }
                }
                return {
                    id: objtos.save.id,
                    texto: objtos.save.texto,
                    marcardo: objtos.save.marcado 
                }
        }
        this.setState({save: this.state.save.map(marcatext)
        })
    }
    
    
    remover(itempararemover) {
        function filterByID(item) {
            if (itempararemover.id != item.id) {
                return true;
            }
            return false;
        }
        this.setState({save: this.state.save.filter(filterByID)
        })
    }
    cadastrar(event) {
        
        event.preventDefault();
        this.setState({save: this.state.save.concat({ id:Math.random() , texto:this.state.itemList })});
    }
    
    render() {
        return (
            <div>
                <form onSubmit={this.cadastrar}>                    
                    <input type="text" value={this.state.itemList} onChange={this.mudarValor}/>
                    <button type="Submit" value="Adicionar">Adicionar</button>
                        </form>
                    <ul>
                    {this.state.save.map(item => (
                            <li key={item.id} style={{
                                textDecoration: 'line-through',
                            }}>{item.texto}
                                <button onClick={() => this.remover(item)}>Remover</button>

                                <button onClick={this.marcar(item)}>Marcar</button>
                            </li>
                        ))}
                    </ul>
            </div>
        );
    }
}
let domContainer = document.querySelector('#button_container');
ReactDOM.render(<List />, document.getElementById("root"));
