import React from "react";
import ReactDOM from "react-dom";

class List extends React.Component  {
    constructor(props){
        super(props);
        this.state = {
            itemList:'',
            saves:[''],
            id:1

        };

        this.mudarValor = this.mudarValor.bind(this);
        this.Alerta = this.Alerta.bind(this);
    }
    
    mudarValor(event) {
        this.setState({itemList: event.target.value})
    };
    
    Alerta(event) {
        alert('Tarefa Listada: ' + this.state.itemList);
        
        event.preventDefault();
        this.setState({saves: this.state.saves.push(event.target.value)})
    }
    
    render() {
        return (
            <div>
            <form onSubmit={this.Alerta}>
            <label>
            Adicionar:
            <input type="text" value={this.state.itemList} onChange={this.mudarValor}/>
            </label>
            <input type="Submit" value="Adicionar"/>
            </form>
           
            </div>
        );
    }
}


let domContainer = document.querySelector('#button_container');
ReactDOM.render(<List />, document.getElementById("root"));

