import ReactDOM from "react-dom";

class LikeButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = { cont: 0 };
    }

    render() {
        return ([
            <text>{this.state.cont}</text>,<br></br>,
            <button onClick={() => this.setState({ cont: cont + 1})}>
                Adicionar
            </button>
        ]);
    }
}
